import { useState } from 'react';
import './App.css';

function App() {
  const [name, setname] = useState("")
  const [email, setemail] = useState("")
  const [gender, setgender] = useState("male")
  const [phonenumber, setphonenumber] = useState("")
  const [passowrd, setpassowrd] = useState("")
  const [username, setUsername] = useState("")
  const [nameError, setnameError] = useState("")
  const [emailError, setemailError] = useState("")
  const [genderError, setgenderError] = useState("")
  const [phonenumberError, setphonenumberError] = useState("")
  const [passowrdError, setpasswordError] = useState("")
  const [emptyError, setemptyError] = useState("")
  const validate =()=>{
    if (
       name === "" || email === "" || gender === "" || phonenumber === "" || passowrd === ""
    ){
        setemptyError("all feilds are mandatory")
        setUsername("")
        return false
    }
    if(!name.match(/^[A-Za-z0-9]+$/)){
         setnameError("Name is not Alphanumeric")
         setUsername("")
         return false 
    }
  }
  const handleSubmit=(e)=>{
    e.preventDefault()
    const isValid = validate()
  }
  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder='name' value={name} onChange = {(e)=>{setname(e.target.value)}}/><br />
        <input type="email" placeholder='Email' value={email} onChange = {(e)=>{setemail(e.target.value)}} /><br />
        <select name="gender" value={gender} onChange = {(e)=>{setgender(e.target.value)}}>
          <option value="Male">Male</option>
          <option value="Female">Female</option>
          <option value="Other">Other</option>
        </select><br />
        <input type="text" placeholder='Phone Number' value={phonenumber} onChange = {(e)=>{setphonenumber(e.target.value)}}/><br />
        <innput type="passowrd" placeholder='Passowrd' value={passowrd} onChange = {(e)=>{setpassowrd(e.target.value)}}/><br />
        <span>{emptyError}</span><br />
        <input type="submit" value="submit" /><br />
      </form>
      <div>
        <h2>{username ? "Hello" + username : ""}</h2>
      </div>
    </div>
  );
}

export default App;
